package net.mcreator.deathnote.procedures;

import net.minecraft.world.level.LevelAccessor;
import net.minecraft.network.chat.Component;

import net.mcreator.deathnote.DeathnoteMod;

public class RevivalSpeechProcedure {
	public static void execute(LevelAccessor world) {
		DeathnoteMod.queueServerWork(20, () -> {
			if (!world.isClientSide() && world.getServer() != null)
				world.getServer().getPlayerList().broadcastSystemMessage(Component.literal(
						"[\u00A75\u0412\u0435\u043B\u0438\u043A\u0438\u0439 \u0434\u0443\u0445 (\u0411\u043E\u0433\u0438\u043D\u044F \u0441\u043C\u0435\u0440\u0442\u0438)\u00A7f] - \u0422\u0432\u043E\u0439 \u043F\u0443\u0442\u044C \u0431\u044B\u043B \u0438\u0441\u043F\u043E\u043B\u043D\u0435\u043D \u0441\u043B\u043E\u0436\u043D\u044B\u0445 \u0438\u0441\u043F\u044B\u0442\u0430\u043D\u0438\u0439 \u0438 \u0436\u0435\u0440\u0442\u0432, \u0438 \u0442\u0435\u043F\u0435\u0440\u044C \u0442\u044B \u0432\u043E\u0437\u0432\u0440\u0430\u0449\u0430\u0435\u0448\u044C\u0441\u044F \u043A \u0436\u0438\u0437\u043D\u0438 \u0431\u043B\u0430\u0433\u043E\u0434\u0430\u0440\u044F \u0441\u0438\u043B\u0435 \u043C\u043E\u0435\u0439 \u043A\u043D\u0438\u0433\u0438 \u0441\u043C\u0435\u0440\u0442\u0438. \u0422\u044B \u043F\u0440\u0435\u043E\u0434\u043E\u043B\u0435\u043B\u0430 \u0441\u0432\u043E\u0438 \u0441\u0442\u0440\u0430\u0445\u0438, \u043D\u0430\u0448\u043B\u0430 \u0432 \u0441\u0435\u0431\u0435 \u0441\u0438\u043B\u044B \u0438 \u043C\u0443\u0434\u0440\u043E\u0441\u0442\u044C, \u0447\u0442\u043E\u0431\u044B \u0441\u043F\u0440\u0430\u0432\u0438\u0442\u044C\u0441\u044F \u0441 \u0442\u0435\u043C\u0438 \u0432\u044B\u0437\u043E\u0432\u0430\u043C\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u043F\u043E\u0441\u0442\u0430\u0432\u0438\u043B\u0430 \u0442\u0435\u0431\u0435 \u0441\u0443\u0434\u044C\u0431\u0430."),
						false);
			DeathnoteMod.queueServerWork(280, () -> {
				if (!world.isClientSide() && world.getServer() != null)
					world.getServer().getPlayerList().broadcastSystemMessage(Component.literal(
							"[\u00A75\u0412\u0435\u043B\u0438\u043A\u0438\u0439 \u0434\u0443\u0445 (\u0411\u043E\u0433\u0438\u043D\u044F \u0441\u043C\u0435\u0440\u0442\u0438)\u00A7f] - \u0422\u0432\u043E\u0435 \u0432\u043E\u0437\u0432\u0440\u0430\u0449\u0435\u043D\u0438\u0435 \u2014 \u044D\u0442\u043E \u043D\u0435 \u0442\u043E\u043B\u044C\u043A\u043E \u0442\u0432\u043E\u044F \u043F\u043E\u0431\u0435\u0434\u0430, \u043D\u043E \u0438 \u0432\u0435\u0447\u043D\u044B\u0439 \u043D\u0430\u043F\u043E\u043C\u0438\u043D\u0430\u043D\u0438\u0435 \u043E \u0437\u043D\u0430\u0447\u0438\u043C\u043E\u0441\u0442\u0438 \u043A\u0430\u0436\u0434\u043E\u0433\u043E \u043C\u0433\u043D\u043E\u0432\u0435\u043D\u0438\u044F \u0436\u0438\u0437\u043D\u0438. \u0422\u0435\u043F\u0435\u0440\u044C \u0442\u044B \u043E\u0431\u0440\u0435\u043B\u0430 \u043D\u043E\u0432\u0443\u044E \u043F\u0435\u0440\u0441\u043F\u0435\u043A\u0442\u0438\u0432\u0443 \u043D\u0430 \u043C\u0438\u0440 \u0438 \u0441\u0432\u043E\u044E \u0441\u043E\u0431\u0441\u0442\u0432\u0435\u043D\u043D\u0443\u044E \u0441\u0443\u0434\u044C\u0431\u0443. \u041E\u0434\u043D\u0430\u043A\u043E \u043F\u043E\u043C\u043D\u0438, \u0447\u0442\u043E \u0441\u0438\u043B\u0430, \u043A\u043E\u0442\u043E\u0440\u0443\u044E \u0442\u044B \u043F\u0440\u0438\u043E\u0431\u0440\u0435\u043B\u0430, \u0442\u0440\u0435\u0431\u0443\u0435\u0442 \u043C\u0443\u0434\u0440\u043E\u0441\u0442\u0438 \u0438 \u043E\u0442\u0432\u0435\u0442\u0441\u0442\u0432\u0435\u043D\u043D\u043E\u0441\u0442\u0438."),
							false);
				DeathnoteMod.queueServerWork(280, () -> {
					if (!world.isClientSide() && world.getServer() != null)
						world.getServer().getPlayerList().broadcastSystemMessage(Component.literal(
								"[\u00A75\u0412\u0435\u043B\u0438\u043A\u0438\u0439 \u0434\u0443\u0445 (\u0411\u043E\u0433\u0438\u043D\u044F \u0441\u043C\u0435\u0440\u0442\u0438)\u00A7f] - \u0421\u043B\u0435\u0434\u0443\u0439 \u0441\u0432\u043E\u0435\u043C\u0443 \u0432\u043E\u0441\u0441\u0442\u0430\u043D\u043E\u0432\u043B\u0435\u043D\u043D\u043E\u043C\u0443 \u043F\u0443\u0442\u0438, \u0443\u0432\u0430\u0436\u0430\u044F \u043A\u0430\u0436\u0434\u0443\u044E \u0436\u0438\u0432\u0443\u044E \u0434\u0443\u0448\u0443 \u0438 \u043F\u043E\u043C\u043D\u044F \u043E \u0442\u043E\u043C, \u0447\u0442\u043E \u0436\u0438\u0437\u043D\u044C \u2014 \u044D\u0442\u043E \u0434\u0430\u0440, \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u043D\u0435\u043E\u0431\u0445\u043E\u0434\u0438\u043C\u043E \u0446\u0435\u043D\u0438\u0442\u044C."),
								false);
				});
			});
		});
	}
}
